import React,{useState} from 'react'
import { Button } from 'react-bootstrap'

const TestBox = ({myColor}) => {

  const [NewColor,serNewColor]= useState(myColor) 
  const changeColor = (e)=>{
    serNewColor(e.target.value)
  }
  const Change = ()=>{
    <div className='colorBoxBody py-5'>
      <div style={{background:{NewColor}}} className='colorBox'></div>
    </div>
  }
  return (
    <div className="" style={{textAlign:"center"}}>
        <input onChange={changeColor} type='text' style={{marginTop:"10px"}}  placeholder='#ColorRGB'/><br/>
        <Button onClick={Change} className='my-3' style={{padding:"3px 15px",borderRadius:"2px"}}>Change Color</Button>
    </div>
  ) 
}

export default TestBox
