import React,{useState} from 'react'
import './colorbox.css'
const ColorBox = ({myColor}) => {
  const [NewColor,serNewColor]= useState(myColor) 
  const changeColor = (e)=>{
    serNewColor(e.target.value)
  }
  return (
    <div>
        <div className='colorBoxBody py-5'>

            <div style={{background:{myColor}}} className='colorBox'></div>


        </div>
    </div>
  )
}

export default ColorBox
