import React, { useState, useEffect } from "react";
import AppNavbar from "./AppNavbar";
import ColorBox from "./ColorBox";
import { Button, Container, Navbar } from "react-bootstrap";
import TestBox from "./TestBox";
import "./colorbox.css";
function App() {
  const [NewColor, serNewColor] = useState();

  const changeColor = (e) => {
    serNewColor(e.target.value);
  };
  return (
    <>
      <Container>

        <Navbar expand="lg" className="bg-body-tertiary">
          <Container>
            <Navbar.Brand href="#home">React Color Change Project</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav"></Navbar.Collapse>
          </Container>
        </Navbar>


          <div className="colorBoxBody py-5">
            <div style={{ background: NewColor }} className="colorBox"></div>
          </div>



        <div className="" style={{ textAlign: "center" }}>
          <input
            onChange={changeColor}
            type="text"
            style={{ marginTop: "10px" }}
            placeholder="#ColorRGB"
          />
          <br />
          <Button
          onClick={()=>NewColor}
            className="my-3"
            style={{ padding: "3px 15px", borderRadius: "2px" }}
          >
            Change Color
          </Button>
        </div>
      </Container>
    </>
  );
}

export default App;
