import React from "react";
import "./colorbox.css";
const ColorBox = ({ NewColor }) => {
  const toUpper = (str) => {
    str.toUpperCase();
  };
  return (
    <div className="colorBoxBody py-5">
      <div style={{ background: NewColor }} className="colorBox">
        <h1 className="text">{toUpperNewColor}</h1>
      </div>
    </div>
  );
};

export default ColorBox;
